mapper.select:
select *
from members
where id = #{id} and password = #{password}

select id
from members
where email = #{email}

mapper.update:
    	update members
		set nickname = #{nickname},
		email = #{email},
		phone = #{phone},
		img = #{img}
		where id = #{id};

    	update members
		set password = #{password}
		where id = #{id};
